import { ref } from 'vue';
import axios from 'axios';
export default {
  name: 'Register',
  setup() {
    const form = ref({
      username: '',
      password: ''
    });
    const onSubmit = async () => {
      try {
        await axios.post('http://localhost:3000/register', form.value);
        alert('User registered successfully');
      } catch (error) {
        console.error(error);
      }
    };
    return {
      form,
      onSubmit
    };
  }
};
